### 4. Handle Real-time Updates

```typescript
import { useRealtimeEvents } from '@/api/hooks/useRealtimeEvents';
import { useQueryClient } from '@tanstack/react-query';

function TaskList() {
  const queryClient = useQueryClient();

  useRealtimeEvents({
    onElementUpdated: (element) => {
      if (element.type === 'task') {
        queryClient.invalidateQueries({ queryKey: ['tasks'] });
      }
    },
  });

  // ... rest of component
}
```

### 5. Add Mutations

```typescript
// apps/web/src/api/hooks/useTaskMutations.ts
import { useMutation, useQueryClient } from '@tanstack/react-query';

const API_BASE = 'http://localhost:3456/api';

export function useUpdateTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: Partial<Task> }) => {
      const res = await fetch(`${API_BASE}/tasks/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updates),
      });
      if (!res.ok) throw new Error('Failed to update');
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['tasks'] });
    },
  });
}

export function useCloseTask() {
  const updateTask = useUpdateTask();

  return useMutation({
    mutationFn: async ({ id, reason }: { id: string; reason?: string }) => {
      return updateTask.mutateAsync({
        id,
        updates: { status: 'closed', closeReason: reason },
      });
    },
  });
}
```

### 6. Create a Page (if needed)

```typescript
// apps/web/src/routes/tasks.tsx
import { TaskCard } from '@/components/task/TaskCard';
import { useAllTasks } from '@/api/hooks/useAllElements';
import { useState } from 'react';

export default function TasksPage() {
  const { data: tasks, isLoading } = useAllTasks();
  const [filter, setFilter] = useState<string>('all');

  if (isLoading) return <div className="p-6">Loading...</div>;

  const filtered = tasks?.filter(t =>
    filter === 'all' || t.status === filter
  );

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Tasks</h1>
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="border rounded px-3 py-2"
        >
          <option value="all">All</option>
          <option value="open">Open</option>
          <option value="in_progress">In Progress</option>
          <option value="closed">Closed</option>
        </select>
      </div>

      <div className="space-y-4">
        {filtered?.map(task => (
          <TaskCard key={task.id} task={task} />
        ))}
      </div>
    </div>
  );
}
```

